import './App.css';
import React, { useEffect, useState } from 'react';


URL = 'http://127.0.0.1:5000/ask?question=Should%20I%20Move%20to%20Los%20Angeles'

export default function MagicEightBall(props) {

  const [userInput, setUserInput] = useState('');
  const [answer, setAnswer] = useState('8');
  const [loading, setLoading] = useState(false);

  const ask = () => {
    setLoading(true);

    fetch(url).then(response => {
      console.log("SUCCESS", response);
      setAnswer(response);
      setLoading(false);
    }).catch(error => {
      console.log(error);
    })
  }

  let body = (answer !== '8') ? answer : <div className='eight'>8</div>
  if (loading) {
    body = "Loading..."
  }

  return (
    <div>

      <div className="question-container">
        <input
          type="text"
          value={userInput}
          onChange={setUserInput}
          disabled={loading}
          />
        <button onClick={ask}>
          Ask the Magic Eight Ball!
        </button>
      </div>

      <div className="ball-container">
        <div className="ball-black-outer">
          <div className="ball-white-inner">
            <div>
              {body}
            </div>
          </div>
        </div>
        <div className="ball-shadow"></div>
      </div>

    </div>
  );

};


class App extends React.Component {
  render() {
    return (
      <MagicEightBall/>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("app"));
